package NewProjectTest.NewProjectTest;

public class PrintedEveryCharTwice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
        String str = "Java";
        
        PrintedEveryCharTwice(str);







	}

	public  static void PrintedEveryCharTwice(String str) {
		// TODO Auto-generated method stub
		
 
	
	for(int i=0;i<str.length();i++)
	{
		
		System.out.print(str.charAt(i)+""+str.charAt(i));
	}


		
		

	}
	

}
